import Vue from 'vue'
import Vuex from 'vuex'
import createPersistedState from "vuex-persistedstate"

Vue.use(Vuex)

export default new Vuex.Store({
  plugins: [createPersistedState({
    storage: window.sessionStorage,
  })],
  state: {
    isAuthenticated: false,
    token: '',
    isLoading: false,
    snackbar: {
      show: false,
      text: '',
      alerttype: '',
      contentclass: '',
    },
    phaseDisplay: {
      show: true
    },
    selectedPlan: '',
  },
  getters: {

  },
  mutations: {
    SNACKBAR_SHOW(state, payload) {
      let timeout = 0
      if (state.snackbar.show) {
        state.snackbar.show = false
        timeout = 300
      }
      setTimeout(() => {
        state.snackbar.show = true
        state.snackbar.text = payload.text
        state.snackbar.alerttype = payload.alerttype
        state.snackbar.contentclass = payload.contentclass
      }, timeout)
    },
    hideSnackbar(state) {
      state.snackbar.show = false
    },
    PHASE_DISPLAY_TOGGLED(state) {
      state.phaseDisplay.show = !state.phaseDisplay.show
    },
    PLAN_SELECTED(state, plan) {
      state.selectedPlan = plan
    }
  },
  actions: {
    selectPlan({ commit }, plan) {
      commit('PLAN_SELECTED', plan)
    },
    togglePhaseDisplay({ commit }) {
      commit('PHASE_DISPLAY_TOGGLED')
    },
    showSnackBar({ commit }, payload) {
      commit('SNACKBAR_SHOW', payload)
    }
  },
  modules: {
  }
})
