import ConfirmDialog from '@/components/dialogs/ConfirmDialog';
export default {
  name: "PlanEditDialog",
  props: {
    plan: Object,
    dialog: Boolean,
    isEdit: Boolean
  },
  data() {
    return {
      valid: false,
      rules: {
        required: value => !!value || "Required."
      }
    };
  },
  methods: {
    save() {
      console.log('isEdit: ', this.isEdit);
      console.log('plan: ', this.plan);
      this.isEdit ? this.$emit("update-plan", this.plan) : this.$emit("create-plan", this.plan);
    },
    async delPlan() {
      if (await this.$refs.confirm.open("Confirm", "Are you sure you want to delete this record?")) {
        console.log('delete it');
        this.deletePlan();
      }
    },
    deletePlan() {
      console.log("Record deleted.");
      this.$emit("delete-plan", this.plan);
    },
    closeDialog() {
      if (!this.isEdit) this.$refs.form.reset();
      this.$emit("close-dialog");
    }
  },
  updated() {
    // this.$refs.form.reset();
    this.$refs.form.resetValidation();
  },
  components: {
    ConfirmDialog
  }
};
