import PlanCard from '@/components/PlanCard';
import axios from 'axios';
export default {
  name: 'PlanList',
  components: {
    PlanCard
  },
  props: {
    plans: Array
  },
  data() {
    return {
      plans: [],
      selectedPlanId: null
    };
  },
  mounted() {
    this.getPlans();
    document.title = 'Home | Digital Wave';
  },
  methods: {
    async getPlans() {
      await axios.get('api/v1/plans/').then(response => {
        this.plans = response.data;
      }).catch(error => {
        console.log(error);
      });
    },
    planClicked(value) {
      this.selectedPlanId = value;
    },
    isActive(plan) {
      if (plan.id === this.selectedPlanId) {
        return true;
      }
      return false;
    }
  }
};
