import "core-js/modules/es.array.push.js";
import PhaseCard from '@/components/phase/PhaseCard';
import PhaseToolbar from '@/components/phase/PhaseToolbar';
import PhaseEditDialog from '@/components/dialogs/PhaseEditDialog';
import axios from 'axios';
export default {
  name: 'PhaseDetailView',
  components: {
    PhaseCard,
    PhaseToolbar,
    PhaseEditDialog
  },
  data() {
    return {
      plan: '',
      isCreateOrDelete: false,
      tab: '',
      // activeName: '',
      isEdit: false,
      open_dialog: false,
      newPhase: {
        id: -1,
        name: '',
        description: '',
        start_date: new Date().toISOString().substring(0, 10),
        end_date: new Date().toISOString().substring(0, 10)
      },
      editPhase: {
        id: 0,
        name: '',
        description: '',
        start_date: '',
        end_date: ''
      }
    };
  },
  computed: {
    selectedPlan() {
      return this.$store.state.selectedPlan;
    },
    selectedPhase() {
      return this.$store.state.selectedPhase.phase;
    },
    show() {
      return this.$store.state.phaseDisplay.show;
    },
    activeName() {
      return this.$store.state.selectedPhase.name;
    }
  },
  watch: {
    tab(newTabURL, oldTabURL) {
      // Set the name of the tab name variable to the first tab in the tablist when the tabs are first shown.
      // oldTabURL will be empty as there will be no previous tab
      if (!oldTabURL) {
        // this.activeName = this.selectedPlan.phases[0].name
        const payload = {
          phase: this.selectedPlan.phases[0],
          name: this.selectedPlan.phases[0].name,
          phaseIndex: 0
        };
        this.$store.dispatch('selectPhase', payload);
      }
    }
  },
  methods: {
    async getPlan() {
      var response = '';
      const plan_slug = this.$route.params.plan_slug;
      try {
        let response = await axios.get(`/api/v1/plan/${plan_slug}`);
      } catch (error) {
        console.log(error);
      }
      // .then(response => {
      //     this.plan = response.data
      //     console.log('plan data getplan: ', response.data)

      // })
      this.$store.commit('PLAN_SELECTED', response.data);
      if (this.isCreateOrDelete) {
        this.$router.push(this.selectedPlan.phases[0].get_absolute_url);
        this.activeName = this.selectedPlan.phases[0].name;
        this.isCreateOrDelete = false;
      }
    },
    async update(updatePayload) {
      // Unpack details from Edit Dialog
      var phaseUpdate = updatePayload.editPhase;
      var isNameChanged = updatePayload.isNameChanged;

      // Find phase by matching id and replace it in the Plan with updated one
      var phaseTabIndex = 0;
      this.$store.state.selectedPlan.phases.forEach((item, index) => {
        if (item.id === phaseUpdate.id) {
          this.$set(this.selectedPlan.phases, index, phaseUpdate);
          phaseTabIndex = index;
        }
      });
      // Update the Plan (now with the updated Phase)
      var response = '';
      try {
        response = await axios.put(`api/v1/plans/${this.selectedPlan.id}/`, this.selectedPlan);
      } catch (error) {
        console.log(error);
      }
      //Save plan to store
      this.$store.dispatch('selectPlan', response.data);

      // Save phase in store
      var phaseResponse = response.data.phases[phaseTabIndex];
      const storePhasePayload = {
        phase: phaseResponse,
        name: phaseResponse.name,
        index: phaseTabIndex
      };
      this.$store.dispatch('selectPhase', storePhasePayload);

      // Make updated tab active if name has changed
      isNameChanged ? this.$router.push(this.selectedPhase.get_absolute_url) : '';

      // Show snackbar
      const snackBarPayload = {
        text: `Successfully updated ${this.selectedPhase.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', snackBarPayload);
    },
    async create(createPhase) {
      // Using the plan update serializer which either updates a phase or create a new one if no phase id
      this.$delete(createPhase, 'id'); // get rid of the plan's id field as we want to update the plan
      this.selectedPlan.phases.push(createPhase); // add the phase to the plan then update the plan

      await axios.put(`api/v1/plans/${this.selectedPlan.id}/`, this.selectedPlan).then(response => {
        this.isCreateOrDelete = true;
        this.getPlan();
        const payload = {
          text: `Successfully created ${createPhase.name}`,
          alerttype: "success",
          contentclass: 'white--text'
        };
        this.$store.dispatch('showSnackBar', payload);
      }).catch(error => {
        console.log(error);
      });
    },
    async del(deletePhase) {
      await axios.delete(`api/v1/phases/${deletePhase.id}/`).then(response => {
        this.isCreateOrDelete = true;
        this.getPlan();
        const payload = {
          text: `Successfully deleted ${deletePhase.name}`,
          alerttype: "success",
          contentclass: 'white--text'
        };
        this.$store.dispatch('showSnackBar', payload);
      }).catch(error => {
        console.log(error);
      });
    },
    openEditDialog(editPhase) {
      this.editPhase = editPhase;
      this.open_dialog = true;
    },
    openNewDialog() {
      this.editPhase = this.newPhase;
      this.open_dialog = true;
    },
    onTabClick(phase, index) {
      const payload = {
        phase: phase,
        name: phase.name,
        phaseIndex: index
      };
      this.$store.dispatch('selectPhase', payload);
      this.activeName = this.$store.state.selectedPhase.name;
      // this.activeName = phase.name
    },

    menuActionClick(index) {
      this.menuItems[index].click.call(this);
    }
  }
};
