import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      createdHeaders: [],
      createdItems: []
    };
  },
  computed: {
    storedCountries: {
      get() {
        return this.$store.state.storedCountries;
      }
    },
    storedChannels: {
      get() {
        return this.$store.state.storedChannels;
      }
    }
  },
  mounted() {
    this.getCountries();
    this.getChannels();
    this.createHeaders();
    this.createDataItems();
  },
  methods: {
    async getCountries() {
      var response = '';
      try {
        response = await axios.get(`/api/v1/countries`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('storeCountries', response.data);
    },
    async getChannels() {
      var response = '';
      try {
        response = await axios.get(`/api/v1/channels`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('storeChannels', response.data);
    },
    createHeaders() {
      var header = {
        text: 'Channel',
        align: 'start',
        sortable: false,
        value: 'code'
      };
      this.createdHeaders.push(header);
      var index = 0;
      this.storedCountries.forEach(country => {
        var header = {
          text: country.code,
          align: 'start',
          sortable: false,
          value: 'countryCode' + index
        };
        this.createdHeaders.push(header);
        index += 1;
      });
      console.log(this.createdHeaders);
    },
    createDataItems() {
      this.storedChannels.forEach(channel => {
        var index = 0;
        var item = {
          'channel': channel.code
        };
        this.storedCountries.forEach(country => {
          var countryCodeLabel = 'countryCode' + index;
          item = Object.assign({
            [countryCodeLabel]: country.code
          }, item);
          index += 1;
        });
        this.createdItems.push(item);
      });
      console.log('createdItems: ', this.createdItems);
    },
    checkClick(item, country) {
      console.log('item: ', item);
      console.log('country: ', country);
    }
  }
};
