import "core-js/modules/es.array.push.js";
import PhaseCard from '@/components/phase/PhaseCard';
import PhaseEditDialog from '@/components/dialogs/PhaseEditDialog';
import NewPhaseEditDialog from '@/components/dialogs/NewPhaseEditDialog';
import axios from 'axios';
export default {
  name: 'PhaseDetailView',
  components: {
    PhaseCard,
    PhaseEditDialog,
    NewPhaseEditDialog
  },
  data() {
    return {
      plan: '',
      tab: 0,
      isEdit: false,
      open_dialog: false,
      newPhase: {
        id: -1,
        name: '',
        description: '',
        start_date: new Date().toISOString().substring(0, 10),
        end_date: new Date().toISOString().substring(0, 10)
      },
      editPhase: {
        id: 0,
        name: '',
        description: '',
        start_date: '',
        end_date: ''
      }
    };
  },
  computed: {
    phases() {
      get: {
        return this.plan.phases;
      }
    }
  },
  mounted() {
    this.getPlan();
  },
  methods: {
    async getPlan() {
      const plan_slug = this.$route.params.plan_slug;
      await axios.get(`/api/v1/plan/${plan_slug}`).then(response => {
        this.plan = response.data;
        document.title = this.plan.name + ' | Digital Wave';
      }).catch(error => {
        console.log(error);
      });
    },
    async updatePhase(updatePhase) {
      this.plan.phases.forEach((item, index) => {
        if (item.id === updatePhase.id) {
          this.$set(this.plan.phases, index, updatePhase);
        }
      });
      await axios({
        method: 'put',
        url: `api/v1/plans/${this.plan.id}/`,
        data: this.plan,
        headers: {
          'Content-Type': 'application/json'
        }
      }).catch(error => {
        console.log(error);
      });
      this.refresh('Successfully deleted', editPhase.name);
      // this.getPlan()
      // const payload = { text: `Successfully updated ${editPhase.name}`, alerttype: "success", contentclass: 'white--text' }
      // this.$store.commit('showSnackBar', payload)
    },

    async createPhase(createPhase) {
      console.log('bef phase: ', createPhase);
      this.$delete(createPhase, 'id');
      console.log('aft phase: ', createPhase);
      this.plan.phases.push(createPhase);
      console.log('plan: ', this.plan);
      await axios({
        method: 'put',
        url: `api/v1/plans/${this.plan.id}/`,
        data: this.plan,
        headers: {
          'Content-Type': 'application/json'
        }
      }).catch(error => {
        console.log(error);
      });
      this.refresh('Successfully deleted', createPhase.name);
      // this.getPlan()
      // this.tab = 0
      // const payload = { text: `Successfully updated ${createPhase.name}`, alerttype: "success", contentclass: 'white--text' }
      // this.$store.commit('showSnackBar', payload)
    },

    async deletePhase(deletePhase) {
      console.log('delete phase id: ', deletePhase.id);
      await axios({
        method: 'delete',
        url: `api/v1/phases/${deletePhase.id}/`,
        data: deletePhase,
        headers: {
          'Content-Type': 'application/json'
        }
      }).catch(error => {
        console.log(error);
      });
      this.refresh('Successfully deleted', deletePhase.name);
      // this.getPlan()
      // this.tab = 0
      // const payload = { text: `Successfully deleted ${deletePhase.name}`, alerttype: "success", contentclass: 'white--text' }
      // this.$store.commit('showSnackBar', payload)
    },

    refresh(message, name) {
      this.getPlan();
      this.tab = 0;
      const payload = {
        text: `${message} ${name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.commit('showSnackBar', payload);
    },
    openEditPhaseDialog(editPhase) {
      this.editPhase = editPhase;
      this.open_dialog = true;
    },
    openNewPhaseDialog() {
      this.editPhase = this.newPhase;
      this.open_dialog = true;
    }
  }
};
