import "core-js/modules/es.array.push.js";
import PlanCard from '@/components/plan/PlanCard';
import PlanEditDialog from '@/components/dialogs/PlanEditDialog';
import axios from 'axios';
export default {
  name: 'HomeView',
  components: {
    PlanCard,
    PlanEditDialog
  },
  data() {
    return {
      plans: [],
      open_dialog: false,
      newPlan: {
        id: -1,
        name: '',
        description: ''
      },
      editPlan: {
        id: 0,
        name: '',
        description: ''
      },
      initialPhase: {
        name: 'Phase name',
        description: 'Phase description',
        start_date: new Date().toISOString().substring(0, 10),
        end_date: new Date().toISOString().substring(0, 10)
      }
    };
  },
  computed: {
    selectedPlan: {
      get() {
        return this.$store.state.selectedPlan;
      }
    },
    selectedPhase: {
      get() {
        return this.$store.state.selectedPhaseData.phase;
      }
    },
    selectedPhaseIndex: {
      get() {
        return this.$store.state.selectedPhaseData.index;
      }
    }
  },
  mounted() {
    this.getPlans();
    document.title = 'Home | Digital Wave';
  },
  methods: {
    async getPlans() {
      var response = '';
      try {
        response = await axios.get('api/v1/plans/');
      } catch (error) {
        console.log(error);
      }
      this.plans = response.data;
      console.log('plan data:', this.plans);
    },
    async getPlan(id) {
      var response = '';
      try {
        response = await axios.get(`/api/v1/plan/${id}`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('selectPlan', response.data);
    },
    async updatePlan(updatePlan) {
      var response = '';
      var planPayload = {
        name: updatePlan.name,
        description: updatePlan.description
      };
      try {
        response = await axios.patch(`api/v1/plan/${updatePlan.id}/`, planPayload);
      } catch (error) {
        console.log(error);
      }
      this.getPlans();
      const payload = {
        text: `Successfully updated ${updatePlan.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    async createPlan(createPlan) {
      var response = '';
      var newPlan = {};
      newPlan = {
        name: createPlan.name,
        description: createPlan.description,
        phases: [this.initialPhase]
      };
      console.log(newPlan);
      try {
        response = await axios.post('api/v1/plans/', newPlan);
      } catch (error) {
        console.log(error);
      }
      this.getPlans();
      const payload = {
        text: `Successfully created ${newPlan.name} - Initial Phase Added`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    async deletePlan(deletePlan) {
      var response = '';
      try {
        response = await axios.delete(`api/v1/plan/${deletePlan.id}/`);
      } catch (error) {
        console.log(error);
      }
      this.getPlans();
      const payload = {
        text: `Successfully deleted ${deletePlan.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    async createPhase(createPhase) {
      // Add current plan to new phase
      createPhase.plan = this.selectedPlan.id;
      var response = '';
      try {
        response = await axios.post(`api/v1/phases/`, createPhase);
      } catch (error) {
        console.log(error);
      }
      // Refresh updated Plan and save Phase to store
      await this.getPlan(this.selectedPlan.id);
      const phaseDataPayload = {
        phase: this.selectedPlan.phases[0],
        index: 0
      };
      this.$store.dispatch('selectPhaseData', phaseDataPayload);
    },
    openEditPlanDialog(editPlan) {
      this.editPlan = editPlan;
      this.open_dialog = true;
    },
    openNewPlanDialog() {
      this.editPlan = this.newPlan;
      this.open_dialog = true;
    },
    checkPlanPhases(plan) {
      if (plan.phases.length === 0) {
        // If it's new plan OR user has deleted all phases then create a dummy phase and save the data to the Store
        this.createPhase(this.initialPhase);
      } else {
        // Save selected Plan to Store
        this.$store.dispatch('selectPlan', plan);
      }
      this.viewPlanDetails();
    },
    viewPlanDetails() {
      document.title = this.$store.state.selectedPlan.name + ' | Digital Wave';
      this.$router.push(this.selectedPhase.get_absolute_url);
    }
  }
};
