import axios from 'axios';
export default {
  data() {
    return {
      headers: [{
        text: 'Name',
        align: 'start',
        sortable: false,
        value: 'name'
      }
      // { text: 'Name', value: 'name' },
      ]
    };
  },

  computed: {
    storedCountries: {
      get() {
        return this.$store.countries;
      }
    },
    storedChannels: {
      get() {
        return this.$store.channels;
      }
    }
  },
  mounted() {
    this.getCountries();
    this.getChannels();
  },
  methods: {
    async getCountries() {
      var response = '';
      try {
        response = await axios.get(`/api/v1/countries`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('storeCountries', response.data);
    },
    async getChannels() {
      var response = '';
      try {
        response = await axios.get(`/api/v1/channels`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('storeChannels', response.data);
    }
  }
};
