import PlanCard from '@/components/PlanCard';
import axios from 'axios';
export default {
  name: 'HomePlans',
  components: {
    PlanCard
  },
  data() {
    return {
      plans: []
    };
  },
  mounted() {
    this.getPlans();
    document.title = 'Home | Digital Wave';
  },
  methods: {
    async getPlans() {
      await axios.get('api/v1/plans/').then(response => {
        this.plans = response.data;
      }).catch(error => {
        console.log(error);
      });
    }
  }
};
