import "core-js/modules/es.array.push.js";
import PhaseCard from '@/components/phase/PhaseCard';
import PhaseToolbar from '@/components/phase/PhaseToolbar';
import PhaseEditDialog from '@/components/dialogs/PhaseEditDialog';
import axios from 'axios';
import store from '@/store';
export default {
  name: 'PlanDetailView',
  components: {
    PhaseCard,
    PhaseToolbar,
    PhaseEditDialog
  },
  data() {
    return {
      plan: '',
      // isCreateOrDelete: false,
      tab: '',
      open_dialog: false,
      newPhase: {
        name: '',
        description: '',
        start_date: new Date().toISOString().substring(0, 10),
        end_date: new Date().toISOString().substring(0, 10)
      },
      editPhase: {
        id: 0,
        name: '',
        description: '',
        start_date: '',
        end_date: ''
      }
    };
  },
  computed: {
    selectedPlan: {
      get() {
        return this.$store.state.selectedPlan;
      }
    },
    selectedPhase: {
      get() {
        return this.$store.state.selectedPhaseData.phase;
      }
    },
    selectedPhaseIndex: {
      get() {
        return this.$store.state.selectedPhaseData.index;
      }
    },
    show: {
      get() {
        return this.$store.state.phaseDisplay.show;
      }
    }
  },
  watch: {
    tab(newTabURL, oldTabURL) {
      // Set the name of the tab name variable to the first tab in the tablist when the tabs are first shown.
      // oldTabURL will be empty as there will be no previous tab
      if (!oldTabURL) {
        const phaseDataPayload = {
          phase: this.selectedPlan.phases[0],
          index: 0
        };
        this.$store.dispatch('selectPhaseData', phaseDataPayload);
      }
    }
  },
  methods: {
    async getPlan() {
      var response = '';
      const plan_slug = this.$route.params.plan_slug;
      try {
        response = await axios.get(`/api/v1/plan/${plan_slug}`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('selectPlan', response.data);
    },
    async updatePhase(updatePayload) {
      var response = '';
      try {
        response = await axios.put(`api/v1/phase/${updatePayload.phase.id}/`, updatePayload.phase);
      } catch (error) {
        console.log(error);
      }
      // Refresh updated Plan and save phase to store
      await this.getPlan();
      const storePhasePayload = {
        phase: response.data,
        index: this.selectedPhase.index
      };
      this.$store.dispatch('selectPhaseData', storePhasePayload);

      // Show snackbar
      this.showSnackBar('updated');

      // Change route if phase name has changed
      updatePayload.changeRoute ? this.$router.push(this.selectedPhase.get_absolute_url) : '';
    },
    async createPhase(createPhase) {
      // Add current plan to new phase
      createPhase.plan = this.selectedPlan.id;
      var response = '';
      try {
        response = await axios.post(`api/v1/phases/`, createPhase);
      } catch (error) {
        console.log(error);
      }
      // Refresh updated Plan and save phase to store
      await this.getPlan();
      const phaseDataPayload = {
        phase: this.selectedPlan.phases[0],
        index: 0
      };
      this.$store.dispatch('selectPhaseData', phaseDataPayload);

      // Display Snackbar message
      this.showSnackBar('created');

      // Change route to selected phase
      this.$router.push(this.selectedPhase.get_absolute_url);
    },
    async deletePhase(deletePhase) {
      var response = '';
      try {
        response = await axios.delete(`api/v1/phases/${deletePhase.id}/`);
      } catch (error) {
        console.log(error);
      }
      // Refresh updated Plan and save phase to store
      this.endDBProcessing(0, 'deleted');
      // await this.getPlan()
      // const phaseDataPayload = { phase: this.selectedPlan.phases[0], index: 0 }
      // this.$store.dispatch('selectPhaseData', phaseDataPayload)

      // Display Snackbar message
      this.showSnackBar('deleted');

      // Change route to selected phase
      // this.$router.push(this.selectedPhase.get_absolute_url)
    },

    async endDBProcessing(phaseIndex, changeType, changeRoute = true) {
      // Refresh updated Plan and save phase to store
      await this.getPlan();
      const phaseDataPayload = {
        phase: this.selectedPlan.phases[phaseIndex],
        index: phaseIndex
      };
      this.$store.dispatch('selectPhaseData', phaseDataPayload);

      // Display Snackbar message
      this.showSnackBar(changeType);

      // Change route to selected phase
      changeRoute ? this.$router.push(this.selectedPhase.get_absolute_url) : '';
    },
    showSnackBar(message) {
      const payload = {
        text: `Successfully ${message} phase ${this.selectedPhase.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    saveToStore(payload) {
      // Unpack payload and save to store
      var plan = payload.plan;
      var phase = payload.phase;
      var index = payload.index;
      var phasePayload = {
        phase: phase,
        index: index
      };
      this.$store.dispatch('selectPlan', plan);
      this.$store.dispatch('selectPhaseData', phasePayload);
    },
    openEditDialog(editPhase) {
      this.editPhase = editPhase;
      this.open_dialog = true;
    },
    openNewDialog() {
      this.editPhase = this.newPhase;
      this.open_dialog = true;
    },
    onTabClick(phase, index) {
      const payload = {
        phase: phase,
        index: index
      };
      this.$store.dispatch('selectPhaseData', payload);
    },
    menuActionClick(index) {
      this.menuItems[index].click.call(this);
    }
  }
};
