export default {
  name: 'PlanEditDialog',
  props: {
    plan: Object,
    dialog: Boolean
  },
  data() {
    return {
      valid: false,
      rules: {
        required: value => !!value || 'Required.'
      }
    };
  },
  computed: {
    isEdit() {
      return this.plan.id > -1 ? true : false;
    }
  },
  methods: {
    save() {
      this.$emit("save", this.plan);
    },
    closeDialog() {
      console.log("isEdit: ", this.isEdit);
      this.$refs.form.resetValidation();
      this.$emit("close-dialog");
    },
    clickOutside() {
      console.log('clicked outside');
    }
  }
};
