import ConfirmDialog from '@/components/dialogs/ConfirmDialog';
export default {
  name: 'PlanEditDialog',
  components: {
    ConfirmDialog
  },
  props: {
    value: Boolean,
    plan: Object
  },
  data() {
    return {
      valid: false,
      editPlan: {},
      rules: {
        required: v => !!v || "Required",
        empty: v => v.length == 0,
        inRange: v => Number.isInteger(Number(v)) && v >= 0 && v <= 10000000 || "Budget should be between 0 and 10,000,000"
      }
    };
  },
  watch: {
    value(val) {
      if (val) {
        this.editPlan = Object.assign({}, this.plan);
        this.$refs.form.resetValidation();
      }
    }
  },
  computed: {
    show: {
      get() {
        return this.value;
      },
      set(value) {
        this.$emit('input', value);
      }
    },
    isEdit: {
      get() {
        if (this.editPlan.id != -1) return true;else return false;
      }
    }
  },
  methods: {
    checkAutoAllocate() {
      console.log('Check AutoAllocate - budget: ', this.editPlan.budget.length);
      if (this.editPlan.budget === 0) {
        this.editPlan.auto_allocate = false;
        console.log('Check AutoAllocate - autoallocate: ', this.editPlan.auto_allocate);
      }
    },
    save() {
      this.isEdit ? this.$emit("update-plan", this.editPlan) : this.$emit("create-plan", this.editPlan);
      this.show = false;
    },
    async delPlan() {
      if (await this.$refs.confirm.open("Confirm", "Are you sure you want to delete this record?")) {
        console.log('delete it');
        this.deletePlan();
      }
    },
    deletePlan() {
      this.$emit("delete-plan", this.editPlan);
      this.show = false;
    }
  }
};
