import ConfirmDialog from '@/components/dialogs/ConfirmDialog';
import DatePicker from '@/components/shared/DatePicker';
export default {
  name: 'PhaseEditDialog',
  components: {
    ConfirmDialog,
    DatePicker
  },
  props: {
    value: Boolean,
    phase: Object
  },
  data() {
    return {
      changeRoute: false,
      valid: false,
      rules: {
        required: value => !!value || "Required."
      },
      editPhase: Object
    };
  },
  watch: {
    value(val) {
      if (val) {
        console.log('phase: ', this.phase);
        this.editPhase = Object.assign({}, this.phase);
        this.$refs.form.resetValidation();
      }
    }
  },
  computed: {
    show: {
      get() {
        return this.value;
      },
      set(value) {
        this.$emit('input', value);
      }
    },
    isEdit: {
      get() {
        if (this.phase.id > -1) return true;else return false;
      }
    }
  },
  methods: {
    save() {
      this.phase.name != this.editPhase.name ? this.changeRoute = true : this.changeRoute = false;
      var payload = {
        phase: this.editPhase,
        changeRoute: this.changeRoute
      };
      this.isEdit ? this.$emit("update-phase", payload) : this.$emit("create-phase", this.editPhase);
      this.show = false;
    },
    async confirmDeletePhase() {
      if (await this.$refs.confirm.open("Confirm", "Are you sure you want to delete this record?")) {
        this.deletePhase();
      }
    },
    deletePhase() {
      this.$emit("delete-phase", this.editPhase);
      this.show = false;
    }
  }
};
