import "core-js/modules/es.array.push.js";
import PlanCard from '@/components/plan/PlanCard';
import PlanEditDialog from '@/components/dialogs/PlanEditDialog';
import axios from 'axios';
import store from '@/store';
export default {
  name: 'HomeView',
  components: {
    PlanCard,
    PlanEditDialog
  },
  data() {
    return {
      viewPlanDetails: false,
      dummyPhaseAdded: false,
      plans: [],
      plan: '',
      open_dialog: false,
      newPlan: {
        id: -1,
        name: '',
        description: ''
      },
      editPlan: {
        id: 0,
        name: '',
        description: ''
      },
      initialPhase: {
        name: 'Phase name',
        description: 'Phase description',
        start_date: new Date().toISOString().substring(0, 10),
        end_date: new Date().toISOString().substring(0, 10)
      }
    };
  },
  mounted() {
    this.getPlans();
    document.title = 'Home | Digital Wave';
  },
  methods: {
    async getPlans() {
      await axios.get('api/v1/plans/').then(response => {
        this.plans = response.data;
        console.log('plan data:', this.plans);
      }).catch(error => {
        console.log(error);
      });
    },
    async updatePlan(editPlan) {
      await axios.put(`api/v1/plans/${editPlan.id}/`, editPlan).then(response => {
        this.$store.dispatch('selectPlan', response.data);
        console.log('updatePlan: .then: ', this.$store.state.selectedPlan);
      }).finally(response => {
        if (this.viewPlanDetails && this.dummyPhaseAdded) console.log('updatePlan: .finally: ', this.$store.state.selectedPlan);
        this.viewPlanDetailsPhaseAdded();
      }).catch(error => {
        console.log(error);
      });
      this.getPlans();
      console.log('this.plans after update: ', this.plans);
      const payload = {
        text: `Successfully updated ${editPlan.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    async createPlan(editPlan) {
      var createPlan = {};
      createPlan = {
        name: editPlan.name,
        description: editPlan.description
      };
      await axios({
        method: 'post',
        url: `api/v1/plans/`,
        data: createPlan,
        headers: {
          'Content-Type': 'application/json'
        }
      }).catch(error => {
        console.log(error);
      });
      this.getPlans();
      const payload = {
        text: `Successfully created ${editPlan.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    async deletePlan(editPlan) {
      await axios({
        method: 'delete',
        url: `api/v1/plans/${editPlan.id}/`,
        data: editPlan,
        headers: {
          'Content-Type': 'application/json'
        }
      }).catch(error => {
        console.log(error);
      });
      this.getPlans();
      const payload = {
        text: `Successfully deleted ${editPlan.name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    openEditPlanDialog(editPlan) {
      this.editPlan = editPlan;
      this.open_dialog = true;
    },
    openNewPlanDialog() {
      this.editPlan = this.newPlan;
      this.open_dialog = true;
    },
    viewPlanDetails(plan) {
      // Save the selected plan in the Store
      this.$store.dispatch('selectPlan', plan);
      console.log('ViewPlanDetails start: ', this.$store.state.selectedPlan);

      // If it's new plan OR user has deleted all phases then create a dummy phase and update the plan
      if (!plan.phases.length) {
        console.log('no phases');
        plan.phases.push(this.initialPhase);
        this.updatePlan(plan);
      } else {
        this.$store.dispatch('selectPlan', plan);
        console.log('after phase # check axiosState:', this.$store.state.axiosState);

        // this.$store.commit('PLAN_SELECTED', this.plan)
        console.log('store plan in view details after update: ', this.$store.state.selectedPlan);
        document.title = this.$store.state.selectedPlan.name + ' | Digital Wave';
        console.log('phase[0] abs url: ', this.$store.state.selectedPlan.phases[0].get_absolute_url);
        this.$router.push(plan.phases[0].get_absolute_url);
      }
    }
  }
};
