import ConfirmDialog from '@/components/dialogs/ConfirmDialog';
export default {
  name: 'NewPhaseEditDialog',
  components: {
    ConfirmDialog
  },
  props: {
    value: Boolean,
    phase: Object
  },
  data() {
    return {
      valid: false,
      rules: {
        required: value => !!value || "Required."
      },
      editPhase: {
        name: '',
        description: '',
        start_date: '',
        end_date: ''
      }
    };
  },
  watch: {
    value(val) {
      if (val) {
        this.editPhase.name = this.phase.name;
        this.editPhase.description = this.phase.description;
        this.editPhase.start_date = this.phase.start_date;
        this.editPhase.end_date = this.phase.end_date;
        this.$refs.form.resetValidation();
      }
    }
  },
  computed: {
    show: {
      get() {
        return this.value;
      },
      set(value) {
        this.$emit('input', value);
      }
    },
    isEdit: {
      get() {
        if (this.phase.id > -1) return true;else return false;
      }
    }
  },
  methods: {
    save() {
      this.phase.name = this.editPhase.name;
      this.phase.description = this.editPhase.description;
      this.isEdit ? this.$emit("update-phase", this.phase) : this.$emit("create-phase", this.phase);
      this.show = false;
    },
    async delPhase() {
      if (await this.$refs.confirm.open("Confirm", "Are you sure you want to delete this record?")) {
        this.deletePhase();
      }
    },
    deletePlan() {
      this.$emit("delete-phase", this.phase);
      this.show = false;
    }
  }
};
