import ConfirmDialog from '@/components/dialogs/ConfirmDialog';
import DatePicker from '@/components/shared/DatePicker';
export default {
  name: "PhaseEditDialog",
  props: {
    phase: Object,
    dialog: Boolean,
    isEdit: Boolean
  },
  components: {
    ConfirmDialog,
    DatePicker
  },
  computed: {
    start_date: {
      get() {
        return this.phase.startDate;
      },
      set(val) {
        this.phase.startDate = val;
      }
    }
  },
  data() {
    return {
      startDate: '',
      valid: false,
      rules: {
        required: value => !!value || "Required."
      }
    };
  },
  methods: {
    save() {
      console.log('isEdit: ', this.isEdit);
      console.log('phase: ', this.phase);
      this.isEdit ? this.$emit("update-phase", this.phase) : this.$emit("create-phase", this.phase);
    },
    async delPhase() {
      if (await this.$refs.confirm.open("Confirm", "Are you sure you want to delete this record?")) {
        console.log('delete it');
        this.deletePhase();
      }
    },
    deletePhase() {
      console.log("Record deleted.");
      this.$emit("delete-phase", this.phase);
    },
    closeDialog() {
      if (!this.isEdit) this.$refs.form.reset();
      this.$emit("close-dialog");
    }
  },
  updated() {
    this.$refs.form.resetValidation();
  }
};
