import "core-js/modules/es.array.push.js";
import PhaseCard from '@/components/phase/PhaseCard';
import PhaseToolbar from '@/components/phase/PhaseToolbar';
import PhaseEditDialog from '@/components/dialogs/PhaseEditDialog';
import axios from 'axios';
export default {
  name: 'PhaseDetailView',
  components: {
    PhaseCard,
    PhaseToolbar,
    PhaseEditDialog
  },
  data() {
    return {
      // plan: '',
      isCreate: false,
      tab: '',
      activeName: '',
      isEdit: false,
      open_dialog: false,
      newPhase: {
        id: -1,
        name: '',
        description: '',
        start_date: new Date().toISOString().substring(0, 10),
        end_date: new Date().toISOString().substring(0, 10)
      },
      editPhase: {
        id: 0,
        name: '',
        description: '',
        start_date: '',
        end_date: ''
      }
    };
  },
  computed: {
    selectedPlan() {
      return this.$store.state.selectedPlan;
    },
    show() {
      return this.$store.state.phaseDisplay.show;
    },
    showSnackBarTruth() {
      return this.$store.state.snackbar.show;
    }
  },
  watch: {
    tab(newTabURL, oldTabURL) {
      // Set the name of the tab name variable to the first tab in the tablist when the tabs are first shown.
      // oldTabURL will be empty as there will be no previous tab
      if (!oldTabURL) {
        this.activeName = this.selectedPlan.phases[0].name;
      }
    },
    showSnackBarTruth() {
      console.log('snackBarTruth: ', this.showSnackBarTruth);
    }
  },
  mounted() {
    // console.log('snackbar show: ', this.$store.state.snackbar)
  },
  methods: {
    async getPlan() {
      const plan_slug = this.$route.params.plan_slug;
      await axios.get(`/api/v1/plan/${plan_slug}`).then(response => {
        this.$store.dispatch('selectPlan', response.data);
      }).catch(error => {
        console.log(error);
      });
      if (this.isCreate) {
        this.$router.push(this.selectedPlan.phases[0].get_absolute_url);
        this.activeName = this.selectedPlan.phases[0].name;
        this.isCreate = false;
      }
    },
    async update(phase) {
      // Find phase by matching id and replace it with updated one
      this.selectedPlan.phases.forEach((item, index) => {
        if (item.id === phase.id) {
          this.$set(this.selectedPlan.phases, index, phase);
        }
      });
      await axios.put(`api/v1/plans/${this.selectedPlan.id}/`, this.selectedPlan).then(response => {
        this.refreshPlanData('Successfully updated', phase.name);
      }).catch(error => {
        console.log(error);
      });
      // this.getPlan()
      // console.log('updated phase')
      // const payload = { text: `Successfully updated ${phase.name}`, alerttype: "success", contentclass: 'white--text' }
      // this.$store.dispatch('showSnackBar', payload)
    },

    async create(phase) {
      // Using the plan update serializer which either updates a phase or create a new one if no phase id

      this.$delete(phase, 'id'); // get rid of the plan's id field as we want to update the plan
      this.selectedPlan.phases.push(phase); // add the phase to the plan then update the plan

      await axios.put(`api/v1/plans/${this.selectedPlan.id}/`, this.selectedPlan).catch(error => {
        console.log(error);
      });
      this.isCreate = true;
      this.refreshPlanData('Successfully created', phase.name);
    },
    async del(phase) {
      console.log('del phase: ', phase.id);
      await axios.delete(`api/v1/phases/${phase.id}/`).catch(error => {
        console.log(error);
      });
      this.refreshPlanData('Successfully delete', phase.name);
    },
    refreshPlanData(message, name) {
      this.getPlan();
      const payload = {
        text: `${message} ${name}`,
        alerttype: "success",
        contentclass: 'white--text'
      };
      this.$store.dispatch('showSnackBar', payload);
    },
    openEditDialog(editPhase) {
      this.editPhase = editPhase;
      this.open_dialog = true;
    },
    openNewDialog() {
      this.editPhase = this.newPhase;
      this.open_dialog = true;
    },
    async onTabClick(phase) {
      this.activeName = phase.name;
    },
    menuActionClick(index) {
      this.menuItems[index].click.call(this);
    }
  }
};
