import PlanCard from '@/components/PlanCard';
import axios from 'axios';
export default {
  name: 'PlanList',
  components: {
    PlanCard
  },
  props: {
    plans: Array
  },
  data() {
    return {
      selectedPlanId: null
    };
  },
  methods: {
    planClicked(value) {
      this.selectedPlanId = value;
    },
    isActive(plan) {
      if (plan.id === this.selectedPlanId) {
        return true;
      }
      return false;
    }
  }
};
