import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      checkBox: [],
      createdHeaders: [],
      createdItems: [],
      checkboxValues: [],
      checked: []
    };
  },
  computed: {
    storedCountries: {
      get() {
        return this.$store.state.storedCountries;
      }
    },
    storedChannels: {
      get() {
        return this.$store.state.storedChannels;
      }
    }
  },
  mounted() {
    this.getCountries();
    this.getChannels();
    this.createHeaders();
    this.createCheckboxValues();
    this.createDataItems();
  },
  watch: {
    checkBox(newValue, oldValue) {
      console.log(newValue);
    }
  },
  methods: {
    async getCountries() {
      var response = '';
      try {
        response = await axios.get(`/api/v1/countries`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('storeCountries', response.data);
    },
    async getChannels() {
      var response = '';
      try {
        response = await axios.get(`/api/v1/channels`);
      } catch (error) {
        console.log(error);
      }
      this.$store.dispatch('storeChannels', response.data);
    },
    createHeaders() {
      var header = {
        text: 'Channel',
        align: 'start',
        sortable: false,
        value: 'channelCode'
      };
      this.createdHeaders.push(header);
      var index = 0;
      this.storedCountries.forEach(country => {
        var header = {
          text: country.code,
          align: 'start',
          sortable: false,
          value: 'countryCode' + index
        };
        this.createdHeaders.push(header);
        index += 1;
      });
      console.log('createdHeaders: ', this.createdHeaders);
    },
    createCheckboxValues() {
      var index = 0;
      this.storedCountries.forEach(country => {
        this.checkboxValues.push('checked' + index);
        index += 1;
      });
    },
    createDataItems() {
      this.storedChannels.forEach(channel => {
        var item = {
          'channelCode': channel.code
        };
        var index = 0;
        this.storedCountries.forEach(country => {
          item = Object.assign({
            [this.checkboxValues[index]]: false
          }, item);
          index += 1;
        });
        this.createdItems.push(item);
      });
      console.log('createdItems: ', this.createdItems);
      console.log('checkboxValues: ', this.checkboxValues);
    },
    checkClick(item, index) {
      var checkObj = {
        channel: item.channelCode,
        country: this.storedCountries[index].code
      };
      this.checked.push(checkObj);
      // console.log('Channel: ', item.channelCode)
      // console.log('Country: ', this.storedCountries[index].code)
    }
  }
};
